import IconData from "../objects/IconData";
import ImageData from "../objects/ImageData";
import fs from 'fs';


export default class ImageUtils {

    public static pathFile: string = './assets/icons/';

    public static iconDatas: IconData[] = [];

    public static toSvgImageFile(base64Data: string, fileName: string): void {
        let encodedData = Buffer.from(base64Data, 'base64');
        let xmlData = encodedData.toString('utf8');

        let options = {encoding:'utf-8', flag:'w'};
        let file = ImageUtils.pathFile + fileName;
        fs.writeFile(file, xmlData, options, err => {
            if (!err) {
                console.log(err);
                return;
            }

            console.log('Create icon successfully with ', fileName);
        });
    }

    public static convertBase64ToFile(base64Data: string, data: ImageData) {
        let sliceSize: number = 512;
        let contentType = '';

        return new File([ImageUtils.base64ToBlob(base64Data, contentType, sliceSize)], 
                        data.fileName, {type: `image/${data.extension}`});
    }

    public static convertImageUrlToFile(imageUrl: string, data: ImageData): File {
        return new File([ImageUtils.convertImageUrlToBlob(imageUrl)], 
                        data.fileName, {type: `image/${data.extension}`});
    }

    /**
     * Convert Image data url to Blob data type
     * 
     * Ex: Input: imageUrl = "data:image/gif;base64,"
     * 
     * @param imageUrl 
     */
    public static convertImageUrlToBlob(imageUrl: string): Blob {
        // split base64 string in data and contentType
        let block = imageUrl.split(";");

        // get the content type of the image
        let contentType = block[0].split(":")[1];   // "image:gif"

        // get the real base64 content of the file
        let realData = block[1].split(",")[1];

        // @ts-ignore
        let blob = this.base64ToBlob(realData, contentType, undefined);
        return blob;
    }

    public static base64ToBlob(base64Data: string, contentType: string, sliceSize: number): Blob {
        contentType = contentType || '';
        sliceSize = sliceSize || 512;

        let byteCharacters = atob(base64Data);
        let byteArrays = [];

        for (let offset = 0; offset < byteCharacters.length; offset += sliceSize) {
            let slice = byteCharacters.slice(offset, offset + sliceSize);
            let byteNumbers = new Array(slice.length);

            for (let i = 0; i < slice.length; ++i) {
                byteNumbers[i] = slice.charCodeAt(i);
            }

            let byteArray = new Uint8Array(byteNumbers);
            byteArrays.push(byteArray);
        }

        let blob = new Blob(byteArrays, {type: contentType});
        return blob;
    }

}